grep -A 1 "Information generated by PeptideAtlas, project" cv_datasources.txt | sed -e 's/[A-Z][A-Z]   //g' | sed -e 's/Information generated by PeptideAtlas, project //' | tr '\n' '=' | tr '-' '#' | sed -e 's/ =/=/g' -e 's/=##=/#/g' -e 's/Alimentary/Digestive/g' -e 's/Thyroid/Thyroid Gland/g' -e 's/Sperm cell/Sperm/g' -e 's/Olfactory System/Olfactory/g' | tr '#' '\n' | grep -v phosphoproteome | sed -e 's/=$//' > PeptideAtlas.mapinfo
